{
  "type": "object",
  "additionalProperties": false,
  "properties": {
      "client_name": { "type": "string" },
      "address": { "type": "string" },
      "email": { "type": "string" },
      "country": { "type": "string" },
      "description": { "type": "string" },
      "rate_per_hour": { "type": "number" }, 
      "hours": { "type": "number" },
      "current_date": { "type": "string"},
      "invoice_number": { "type": "string" },
      "subtotal": { 
        "type": "number",
        "description": "total before tax"
      },
      "tax": {
        "type": "object",
        "properties": {
          "rate": { "type": "number" },
          "amount": { "type": "number" }
      },
      "required": ["rate", "amount"]
      },
      "grand_total": {
        "type": "number",
        "description": "final total after tax"
      }
    },
   "required": ["client_name"]
}

